mapper.select:
SELECT ME_CCUSTOMERID_CK ,ME_CCOMPANYID ,ME_CEMPLOYEEID_CK ,main.ME_CUSERID ,main.ME_DSTARTDATE ,ME_CKANANAME ,CASE WHEN ME_DSTARTDATE > TRUNC(SYSDATE) THEN PSMASTER.FUNC_GET_EMP_NAME('${custId}',ME_CCOMPANYID,ME_CEMPLOYEEID_CK,'2222/03/31','${language}') ELSE PSMASTER.FUNC_GET_EMP_NAME('${custId}',ME_CCOMPANYID,ME_CEMPLOYEEID_CK,NULL,'${language}') END ME_CNAME ,ME_CMAIL ,ME_DDATEOFEMPLOYEMENT ,ME_DDATEOFRETIREMENT ,ME_ID ,MA_ID ,MA_CUSERID ,MA_CACCOUNT ,MA_DSTART ,MA_DEND ,MA_NRETRYCOUNTER ,MA_NPASSWORDLOCK ,MA_CADMINUSER ,em.TMA_EMAIL ,MAST_ACCOUNT.VERSIONNO VERSIONNO FROM MAST_EMPLOYEES main, MAST_ACCOUNT, ( SELECT MIN(ME_DSTARTDATE) ,ME_CUSERID FROM MAST_EMPLOYEES WHERE TRUNC(ME_DENDDATE) >= TRUNC(SYSDATE) GROUP BY ME_CUSERID ) mindate, T_EMPLOY_MAIL em WHERE ME_CCUSTOMERID_CK =#{custId} AND mindate."MIN(ME_DSTARTDATE)" = main.ME_DSTARTDATE AND mindate.ME_CUSERID = main.ME_CUSERID
AND ME_CCOMPANYID = #{companyId}
AND ME_CCOMPANYID IN
#{compId}
AND main.ME_CUSERID IN ( SELECT HD_CUSERID FROM HIST_DESIGNATION WHERE HD_CCUSTOMERID_CK = ME_CCUSTOMERID_CK AND HD_CCOMPANYID_CK = ME_CCOMPANYID AND HD_CEMPLOYEEID_CK = ME_CEMPLOYEEID_CK AND HD_DENDDATE >= TRUNC(SYSDATE) AND HD_CEMPLOYEEID_CK LIKE '%' || #{empId} || '%' ) AND ME_CCUSTOMERID_CK = MA_CCUSTOMERID(+) AND main.ME_CUSERID = MA_CUSERID(+) AND em.TMA_EMP_ID = main.ME_CEMPLOYEEID_CK(+) ORDER BY ME_CEMPLOYEEID_CK
