mapper.select:
select
    me.me_ddateofemployement
from
    mast_employees me
where
    me.me_ccustomerid_ck  = #{customerId}
and me.me_ccompanyid      = #{companyId}
and me.me_cemployeeid_ck  = #{employeeId}
and me.me_dstartdate <= #{yyyymmdd,jdbcType=DATE}
and me.me_denddate >= #{yyyymmdd,jdbcType=DATE}


    SELECT
        -- (職員検索ダイアログ)
        HD.HD_ID,
        ME_CUSERID,
        (
            SELECT
                MAP_NWEIGHTAGE
            FROM
                MAST_POST
            WHERE
                MAP_CCUSTOMERID_CK_FK = HD.HD_CCUSTOMERID_CK
                AND MAP_CCOMPANYID_CK_FK = HD.HD_CCOMPANYID_CK
                AND MAP_CPOSTID_CK = HD.HD_CPOSTID_FK
                AND MAP_CLANGUAGE = 'ja'
                AND MAP_DSTART <= #{searchDate}
                AND MAP_DEND >= #{searchDate}
        ) AS MAP_NWEIGHTAGE,
        ME_CIFSTILLEMPLOYEDID,
        ME_CEMPLOYEEID_CK,
        ME_CKANANAME,
        HD_CBOSSORNOT,
        HD_DSTARTDATE_CK,
        HD_CPOSTID_FK
    FROM
        (
            SELECT /*+ INDEX(MAST_EMPLOYEES MAST_EMPLOYEES_IDX4) */*
            FROM MAST_EMPLOYEES
            WHERE
                ME_CCUSTOMERID_CK = '01'
                AND ME_DSTARTDATE <= #{searchDate}
                AND ME_DENDDATE >= #{searchDate}
                AND ME_CIFSTILLEMPLOYEDID = '0'
        ) ME,
        (
            SELECT /*+ INDEX(HIST_DESIGNATION PS_HIST_DESIGNATION_IDX5) */*
            FROM ${designation}
            WHERE
                HD_CCUSTOMERID_CK = '01'
                AND HD_DSTARTDATE_CK <= #{searchDate}
                AND HD_DENDDATE >= #{searchDate}
        ) HD
    WHERE
         HD.HD_CUSERID = ME_CUSERID
         AND (
             ME_CEMPLOYEEID_CK LIKE #{searchWord}
             OR ME_CKANANAME LIKE #{searchWordConve}
             OR ME_CKANJINAME LIKE #{searchWord}
             OR LOWER(ME_CENGLISHNAME) LIKE #{searchWordEnglish}
             OR ME_CEMPLOYEENAMECH LIKE #{searchWord}
             OR ME_CEMPLOYEENAME01 LIKE #{searchWord}
             OR ME_CEMPLOYEENAME02 LIKE #{searchWord}
         )

            AND ME_CCOMPANYID IN

                #{companyId}



            AND HD.HD_CCOMPANYID_CK = #{companyCode}


             AND HD.HD_CIFKEYORADDITIONALROLE = #{ifKeyorAdditionalRole}

         ${exists}


    UNION ALL


    SELECT
        NULL AS HD_ID,
        ME_CUSERID,
        NULL MAP_NWEIGHTAGE,
        ME_CIFSTILLEMPLOYEDID,
        ME_CEMPLOYEEID_CK,
        ME_CKANANAME,
        NULL AS HD_CBOSSORNOT,
        NULL AS HD_DSTARTDATE_CK,
        NULL AS HD_CPOSTID_FK
    FROM
        (
            SELECT /*+ INDEX(MAST_EMPLOYEES MAST_EMPLOYEES_IDX4) */*
                FROM MAST_EMPLOYEES
                WHERE
                      ME_CCUSTOMERID_CK = '01'
                      AND ME_DSTARTDATE <= #{searchDate}
                      AND ME_DENDDATE >= #{searchDate}
                      AND ME_CIFSTILLEMPLOYEDID = '1'
        )
    WHERE
        (
            ME_CEMPLOYEEID_CK LIKE #{searchWord}
            OR ME_CKANANAME LIKE #{searchWordConve}
            OR ME_CKANJINAME LIKE #{searchWord}
            OR LOWER(ME_CENGLISHNAME) LIKE #{searchWordEnglish}
            OR ME_CEMPLOYEENAMECH LIKE #{searchWord}
            OR ME_CEMPLOYEENAME01 LIKE #{searchWord}
            OR ME_CEMPLOYEENAME02 LIKE #{searchWord}
        )

            AND ME_CCOMPANYID IN

                #{companyId}



            AND ME_CCOMPANYID = #{companyCode}

        ${exists}

${orderBy}

SELECT
    HD_ID,
    HD_CUSERID AS ME_CUSERID,
    (
        SELECT
            MAP_NWEIGHTAGE
        FROM
            MAST_POST
        WHERE
            MAP_CCUSTOMERID_CK_FK = HD_CCUSTOMERID_CK
            AND MAP_CCOMPANYID_CK_FK = HD_CCOMPANYID_CK
            AND MAP_CPOSTID_CK = HD_CPOSTID_FK
            AND MAP_CLANGUAGE = 'ja'
            AND MAP_DSTART <= #{searchDate}
            AND MAP_DEND >= #{searchDate}
    ) AS MAP_NWEIGHTAGE
FROM MAST_EMPLOYEES,${designation} HD
WHERE
    HD_CCUSTOMERID_CK = '01'
    AND HD_DSTARTDATE_CK <= #{searchDate}
    AND HD_DENDDATE >= #{searchDate}
    AND HD_CCOMPANYID_CK = #{targetComp}
    AND HD_CSECTIONID_FK = #{targetDept}
    AND ME_CCUSTOMERID_CK = HD_CCUSTOMERID_CK
    AND ME_CUSERID = HD_CUSERID
    AND ME_DSTARTDATE <= #{searchDate}
    AND ME_DENDDATE >= #{searchDate}

        AND (

                ME_CEMPLOYEEID_CK LIKE #{searchWord}
                OR ME_CKANJINAME LIKE #{searchWord}
                OR ME_CEMPLOYEENAMECH LIKE #{searchWord}
                OR ME_CEMPLOYEENAME01 LIKE #{searchWord}
                OR ME_CEMPLOYEENAME02 LIKE #{searchWord}


                OR LOWER(ME_CENGLISHNAME) LIKE #{searchWordEnglish}


                OR ME_CKANANAME LIKEC #{searchWordConve}

        )


        AND	ME_CIFSTILLEMPLOYEDID = 0


        AND	ME_CIFSTILLEMPLOYEDID = #{searchFlg}

    ${exists}
    ${orderBy}

SELECT ME_CCUSTOMERID_CK, ME_CCOMPANYID, ME_CEMPLOYEEID_CK, main.ME_CUSERID, main.ME_DSTARTDATE, ME_CKANANAME, CASE WHEN ME_DSTARTDATE > TRUNC(SYSDATE) THEN PSMASTER.FUNC_GET_EMP_NAME('${custId}',ME_CCOMPANYID,ME_CEMPLOYEEID_CK,'2222/03/31','${language}') ELSE PSMASTER.FUNC_GET_EMP_NAME('${custId}',ME_CCOMPANYID,ME_CEMPLOYEEID_CK,NULL,'${language}') END ME_CNAME, ME_CMAIL, ME_ID, ME_DDATEOFEMPLOYEMENT, ME_DDATEOFRETIREMENT, MA_ID, MA_CUSERID, MA_CACCOUNT, MA_DSTART, MA_DEND, MA_NRETRYCOUNTER, MA_NPASSWORDLOCK, MA_CADMINUSER, MAST_ACCOUNT.VERSIONNO VERSIONNO FROM MAST_EMPLOYEES main, ( SELECT MAP_CUSERID FROM MAST_PASSWORD WHERE MAP_NHISTORY = 0),MAST_ACCOUNT, ( SELECT MIN(ME_DSTARTDATE), ME_CUSERID FROM MAST_EMPLOYEES WHERE TRUNC(ME_DENDDATE) >= TRUNC(SYSDATE) GROUP BY ME_CUSERID ) mindate WHERE ME_CCUSTOMERID_CK = #{custId} AND main.ME_CUSERID = MAP_CUSERID(+) AND mindate."MIN(ME_DSTARTDATE)" = main.ME_DSTARTDATE AND mindate.ME_CUSERID = main.ME_CUSERID AND ME_CCUSTOMERID_CK = MA_CCUSTOMERID(+) AND main.ME_CUSERID = MA_CUSERID(+)
AND ME_CCOMPANYID = #{companyId}
AND ME_CCOMPANYID IN
#{compId}
ORDER BY ME_CEMPLOYEEID_CK

SELECT ME_CCUSTOMERID_CK, ME_CCOMPANYID, ME_CEMPLOYEEID_CK, main.ME_CUSERID, main.ME_DSTARTDATE, ME_CKANANAME, CASE WHEN ME_DSTARTDATE > TRUNC(SYSDATE) THEN PSMASTER.FUNC_GET_EMP_NAME('${custId}',ME_CCOMPANYID,ME_CEMPLOYEEID_CK,'2222/03/31','${language}') ELSE PSMASTER.FUNC_GET_EMP_NAME('${custId}',ME_CCOMPANYID,ME_CEMPLOYEEID_CK,NULL,'${language}') END ME_CNAME, ME_CMAIL, ME_DDATEOFEMPLOYEMENT, ME_DDATEOFRETIREMENT, ME_ID, MA_ID, MA_CUSERID, MA_CACCOUNT, MA_DSTART, MA_DEND, MA_NRETRYCOUNTER, MA_NPASSWORDLOCK, MA_CADMINUSER, MAST_ACCOUNT.VERSIONNO VERSIONNO FROM MAST_EMPLOYEES main, ( SELECT * FROM MAST_ACCOUNT WHERE ( TRUNC(SYSDATE) >= MA_DSTART AND TRUNC(SYSDATE) <= MA_DEND) AND MA_NPASSWORDLOCK = 1 ) MAST_ACCOUNT, ( SELECT MIN(ME_DSTARTDATE) ,ME_CUSERID FROM MAST_EMPLOYEES WHERE TRUNC(ME_DENDDATE) >= TRUNC(SYSDATE) GROUP BY ME_CUSERID ) mindate WHERE ME_CCUSTOMERID_CK = #{custId} AND ME_CCUSTOMERID_CK = MA_CCUSTOMERID AND main.ME_CUSERID = MA_CUSERID AND mindate."MIN(ME_DSTARTDATE)" = main.ME_DSTARTDATE AND mindate.ME_CUSERID = main.ME_CUSERID
AND ME_CCOMPANYID = #{companyId}
AND ME_CCOMPANYID IN
