mapper.select:
select t0.cust ,t0.comp ,t0.empid ,t0.empname ,tmg_f_get_pattern_default(t0.cust, t0.comp, t0.empid, to_date(#{baseDate}, 'yyyy/mm/dd')) .cpatternid as tpaa_cpatternid ,tmg_f_get_pattern_default(t0.cust, t0.comp, t0.empid, to_date(#{baseDate}, 'yyyy/mm/dd')) .cpatternname as tpa_cpatternname ,tmg_get_worktypeid(t0.cust, t0.comp, t0.empid, to_date(#{baseDate}, 'yyyy/mm/dd')) as cworktypeid ,tmg_f_get_mgd(tmg_get_worktypeid(t0.cust, t0.comp, t0.empid,to_date(#{baseDate}, 'yyyy/mm/dd')), to_date(#{baseDate},'yyyy/mm/dd'), t0.cust, t0.comp, #{language}) as cworktypename ,(select decode(count(1), 0, 0, 1) from tmg_personal_pattern where tppa_ccustomerid = t0.cust and tppa_ccompanyid = t0.comp and tppa_cemployeeid = t0.empid and to_date(#{baseDate}, 'yyyy/mm/dd') between tppa_dstartdate and tppa_denddate and (tppa_nkinmu_num1 is not null or tppa_nkinmu_num2 is not null or tppa_nkinmu_num3 is not null or tppa_nkinmu_num4 is not null or tppa_nkinmu_num5 is not null)) as personal_pattern_use from (
${targetEmployees}
) t0 where exists (select 1 from tmg_group_member where tgrm_ccustomerid = t0.cust and tgrm_ccompanyid = t0.comp and tgrm_cemployeeid = t0.empid and tgrm_dstartdate <= to_date(#{baseDate}, 'yyyy/mm/dd') and tgrm_denddate >= to_date(#{baseDate}, 'yyyy/mm/dd')) order by empid;

select ccustomerid as tpa_ccustomerid ,ccompanyid as tpa_ccompanyid ,csectionid as tpa_csectionid ,cgroupid as tpa_cgroupid ,dstartdate as tpa_dstartdate ,denddate as tpa_denddate ,cpatternid as tpa_cpatternid ,cpatternname as tpa_cpatternname ,cdefaultflg as tpa_cdefaultflg ,tmg_f_timerange_table_json(timerange) as timerange ,tmg_f_conv_min2hhmi(ndate_change_time, 0) as tpa_ndate_change_time ,c2caldays as tpa_c2caldays ,cnextptn as tpa_cnextptn from table(tmg_f_get_tmg_pattern_list(#{custId},#{compCode}, #{groupId}, null, trunc(sysdate)))
where cpatternid<> #{patternId}
order by csectionid, cgroupid, cpatternid

mapper.delete:
delete from tmg_pattern
where tpa_ccustomerid = '01'
and tpa_ccompanyid = '01'

    and tpa_csectionid = #{sectionId}


    and tpa_csectionid is null


    and tpa_cgroupid = #{groupId}


    and tpa_cgroupid is null

and tpa_cpatternid = #{patternId}

delete from tmg_pattern_rest
where tpr_ccustomerid = '01'
and tpr_ccompanyid = '01'

    and tpr_csectionid = #{sectionId}


    and tpr_csectionid is null


    and tpr_cgroupid = #{groupId}


    and tpr_cgroupid is null

and tpr_cpatternid = #{patternId}

delete from tmg_pattern_applies
where tpaa_ccustomerid = '01'
and tpaa_ccompanyid = '01'

    and tpaa_csectionid = #{sectionId}


    and tpaa_csectionid is null


    and tpaa_cgroupid = #{groupId}


    and tpaa_cgroupid is null

and tpaa_cpatternid = #{patternId}
