mapper.select:
select m.menu_id, m.url, m.parent_menu_id, m.type, m.order_num, m.level, m.icon, m.perms, m.ja_name, m.en_name, m.zh_name, m.customer_id, m.company_id, m.remark, m.dispatcher_button, m.page_id from t_group_menu gm join mast_group g on g.mg_cgroupid_pk = gm.group_code join t_menu m on m.menu_id = gm.menu_id
and gm.start_date <= current_date and gm.end_date >= current_date and gm.customer_id = #{customerId} and gm.company_id = #{systemCode} and g.mg_cgroupid_pk in
#{groupCode}
