mapper.select:
select mgd_ndefault_month
,to_char(tmg_f_get_the_yearzone(to_number(tmg_f_get_the_year(to_date(#{firstDayOfYear}, 'yyyy/mm/dd'),mgd_ccustomerid, mgd_ccompanyid)), '1', to_date(#{firstDayOfYear}, 'yyyy/mm/dd'), mgd_ccustomerid,mgd_ccompanyid), 'yyyy') || '/' || lpad(v.mgd_ndefault_month, 2, '0') || '/01' as dispterm_start
,to_char(add_months(to_date(to_char(tmg_f_get_the_yearzone(to_number(tmg_f_get_the_year(to_date(#{firstDayOfYear}, 'yyyy/mm/dd'), mgd_ccustomerid, mgd_ccompanyid)), '1',to_date(#{firstDayOfYear}, 'yyyy/mm/dd'), mgd_ccustomerid, mgd_ccompanyid), 'yyyy') ||'/' || lpad(v.mgd_ndefault_month , 2, '0') || '/01'),11), 'yyyy/mm/dd') as dispterm_end
from tmg_v_mgd_first_month v
where v.mgd_ccustomerid = #{custId}
and v.mgd_ccompanyid = #{compCode}
and v.mgd_dstart <=  to_date(#{dyyyymmdd}, 'yyyy/mm/dd')
and v.mgd_dend >= to_date(#{dyyyymmdd}, 'yyyy/mm/dd')
and v.mgd_clanguage = #{language}
and v.mgd_cworkertype = tmg_f_get_workertype(#{employeeId}, to_date(#{dyyyymmdd}, 'yyyy/mm/dd'), mgd_ccustomerid, mgd_ccompanyid)

select to_char(tph_dyyyymmdd, 'mm') || '月' || to_char(tph_dyyyymmdd,'dd') || '日' endueDate,
nvl(tph_ninvest, 0) + nvl(tph_nthroughout, 0) + nvl(tph_nadjust,0) + nvl(tph_nadjust_to, 0) endueDays,
tmg_f_conv_min2hhmi(nvl(tph_nthroughout_hours, 0) + nvl(tph_nadjust_hours, 0) + nvl(tph_nadjust_hours_to, 0), 1) endueHours
from tmg_paid_holiday
where tph_cemployeeId = #{employeeId}
and tph_dyyyymm = (select max(tph_dyyyymm) from tmg_paid_holiday
where tph_cemployeeId = #{employeeId}
and tph_dyyyymm >= tmg_f_get_the_yearzone(to_number(tmg_f_get_the_year(to_date(#{preMonthDay}, 'yyyy/mm/dd'),#{custId}, #{compCode})), 0, #{dyyyymmdd}, #{custId}, #{compCode})
and tph_dyyyymm <= tmg_f_get_the_yearzone(to_number(tmg_f_get_the_year(to_date(#{nextYearDay}, 'yyyy/mm/dd'),#{custId}, #{compCode})), 1, #{dyyyymmdd}, #{custId}, #{compCode}))
and tph_ccompanyid = #{compCode}

select '01' tmaCcustomerid ,'01' tmaCcompanyid ,#{employeeId} tmaCemployeeid ,to_char(tma_dyyyymm,'yyyy') dyyyy ,to_char(tma_dyyyymm,'MM') dmm ,tma_dyyyymm ,tma_worktypeid01 ,nvl(tma_workcontent01, ' ') tma_workcontent01 ,tma_worktypeid02 ,nvl(tma_workcontent02, ' ') tma_workcontent02 ,tma_worktypeid03 ,nvl(tma_workcontent03, ' ') tma_workcontent03 ,tma_worktypeid04 ,nvl(tma_workcontent04, ' ') tma_workcontent04 ,tma_worktypeid05 ,nvl(tma_workcontent05, ' ') tma_workcontent05 ,tma_worktypeid06 ,nvl(tma_workcontent06, ' ') tma_workcontent06 ,tma_worktypeid07 ,nvl(tma_workcontent07, ' ') tma_workcontent07 ,tma_worktypeid08 ,nvl(tma_workcontent08, ' ') tma_workcontent08 ,tma_worktypeid09 ,nvl(tma_workcontent09, ' ') tma_workcontent09 ,tma_worktypeid10 ,nvl(tma_workcontent10, ' ') tma_workcontent10 ,tma_worktypeid11 ,nvl(tma_workcontent11, ' ') tma_workcontent11 ,tma_worktypeid12 ,nvl(tma_workcontent12, ' ') tma_workcontent12 ,tma_worktypeid13 ,nvl(tma_workcontent13, ' ') tma_workcontent13 ,tma_worktypeid14 ,nvl(tma_workcontent14, ' ') tma_workcontent14 ,tma_worktypeid15 ,nvl(tma_workcontent15, ' ') tma_workcontent15 ,tma_worktypeid16 ,nvl(tma_workcontent16, ' ') tma_workcontent16 ,tma_worktypeid17 ,nvl(tma_workcontent17, ' ') tma_workcontent17 ,tma_worktypeid18 ,nvl(tma_workcontent18, ' ') tma_workcontent18 ,tma_worktypeid19 ,nvl(tma_workcontent19, ' ') tma_workcontent19 ,tma_worktypeid20 ,nvl(tma_workcontent20, ' ') tma_workcontent20 ,tma_worktypeid21 ,nvl(tma_workcontent21, ' ') tma_workcontent21 ,tma_worktypeid22 ,nvl(tma_workcontent22, ' ') tma_workcontent22 ,tma_worktypeid23 ,nvl(tma_workcontent23, ' ') tma_workcontent23 ,tma_worktypeid24 ,nvl(tma_workcontent24, ' ') tma_workcontent24 ,tma_worktypeid25 ,nvl(tma_workcontent25, ' ') tma_workcontent25 ,tma_worktypeid26 ,nvl(tma_workcontent26, ' ') tma_workcontent26 ,tma_worktypeid27 ,nvl(tma_workcontent27, ' ') tma_workcontent27 ,tma_worktypeid28 ,nvl(tma_workcontent28, ' ') tma_workcontent28 ,tma_worktypeid29 ,nvl(tma_workcontent29, ' ') tma_workcontent29 ,tma_worktypeid30 ,nvl(tma_workcontent30, ' ') tma_workcontent30 ,tma_worktypeid31 ,nvl(tma_workcontent31, ' ') tma_workcontent31
${params}
from tmg_attendance_book, tmg_monthly where tma_cemployeeid = #{employeeId} and tma_ccompanyid = '01' and tma_dyyyymm >= to_date(#{curMonthDay}, 'yyyy/mm/dd') and tma_dyyyymm <= to_date(#{nextYearDay}, 'yyyy/mm/dd') and tma_ccustomerid = '01' and tmo_ccustomerid = tma_ccustomerid and tmo_ccompanyid = tma_ccompanyid and tmo_cemployeeid = tma_cemployeeid and trunc(tmo_dyyyymm) = trunc(tma_dyyyymm) union all select '01' tmaCcustomerid ,'01' tmaCcompanyid ,#{employeeId} tmaCemployeeid ,to_char(tma_dyyyymm,'yyyy') dyyyy ,to_char(tma_dyyyymm,'MM')||'月' dmm ,b.tma_dyyyymm ,b.tma_worktypeid01 ,b.tma_workcontent01 ,b.tma_worktypeid02 ,b.tma_workcontent02 ,b.tma_worktypeid03 ,b.tma_workcontent03 ,b.tma_worktypeid04 ,b.tma_workcontent04 ,b.tma_worktypeid05 ,b.tma_workcontent05 ,b.tma_worktypeid06 ,b.tma_workcontent06 ,b.tma_worktypeid07 ,b.tma_workcontent07 ,b.tma_worktypeid08 ,b.tma_workcontent08 ,b.tma_worktypeid09 ,b.tma_workcontent09 ,b.tma_worktypeid10 ,b.tma_workcontent10 ,b.tma_worktypeid11 ,b.tma_workcontent11 ,b.tma_worktypeid12 ,b.tma_workcontent12 ,b.tma_worktypeid13 ,b.tma_workcontent13 ,b.tma_worktypeid14 ,b.tma_workcontent14 ,b.tma_worktypeid15 ,b.tma_workcontent15 ,b.tma_worktypeid16 ,b.tma_workcontent16 ,b.tma_worktypeid17 ,b.tma_workcontent17 ,b.tma_worktypeid18 ,b.tma_workcontent18 ,b.tma_worktypeid19 ,b.tma_workcontent19 ,b.tma_worktypeid20 ,b.tma_workcontent20 ,b.tma_worktypeid21 ,b.tma_workcontent21 ,b.tma_worktypeid22 ,b.tma_workcontent22 ,b.tma_worktypeid23 ,b.tma_workcontent23 ,b.tma_worktypeid24 ,b.tma_workcontent24 ,b.tma_worktypeid25 ,b.tma_workcontent25 ,b.tma_worktypeid26 ,b.tma_workcontent26 ,b.tma_worktypeid27 ,b.tma_workcontent27 ,b.tma_worktypeid28 ,b.tma_workcontent28 ,b.tma_worktypeid29 ,b.tma_workcontent29 ,b.tma_worktypeid30 ,b.tma_workcontent30 ,b.tma_worktypeid31 ,b.tma_workcontent31 ,' ' ,' ' ,' ' ,' ' ,' ' ,' ' ,' ' ,' ' ,' ' ,' ' ,' ' from table(tmg_f_get_attendance_book_list('01', '01', #{employeeId}, to_date(#{curMonthDay}, 'yyyy/mm/dd'), to_date(#{nextYearDay},'yyyy/mm/dd'))) b where b.tma_ccustomerid = '01' and b.tma_ccompanyid = '01' and b.tma_dyyyymm >= to_date(#{curMonthDay}, 'yyyy/mm/dd') and b.tma_dyyyymm <= to_date(#{nextYearDay}, 'yyyy/mm/dd') and not exists(select 1 from tmg_attendance_book c where c.tma_ccustomerid = b.tma_ccustomerid and c.tma_ccompanyid = b.tma_ccompanyid and c.tma_cemployeeid = #{employeeId} and c.tma_dyyyymm = b.tma_dyyyymm) order by tma_dyyyymm

select tmy_comment from tmg_yearly
 where tmy_cemployeeid = #{employeeId}
   and tmy_nyyyy = to_number(tmg_f_get_the_year(to_date(#{yearLastDay},'yyyy/mm/dd'), #{custId}, #{compCode}))
   and tmy_ccompanyid = #{compCode}
   and tmy_ccustomerid = #{custId};
