mapper.select:
SELECT
    SUM(DECODE(TDA_CSTATUSFLG, NULL, NULL, #{dataStatus9}, 0, #{dataStatus5} , 0, 1))
FROM
    TMG_DAILY
WHERE TDA_CCOMPANYID = #{compId}
AND TDA_CCUSTOMERID = #{custId}
AND TDA_CEMPLOYEEID = #{empId}
AND TDA_DYYYYMM = TRUNC(TO_DATE(#{targetDate}, 'YYYY/MM/DD'))

SELECT to_char(TDA_DYYYYMMDD, 'yyyy/MM/dd') AS TDA_DYYYYMMDD, to_char(TDA_DYYYYMMDD, 'dd') AS TDA_DYYYYMMDD_DD, to_char(TDA_DYYYYMMDD, 'dy') AS TDA_DYYYYMMDD_DY, TDA_CSTATUSFLG, ( SELECT TMG_F_GET_MGD_C(TDA_CSTATUSFLG, TDA_DYYYYMMDD, 1, TDA_CCUSTOMERID,TDA_CCOMPANYID, 'ja') TDA_CSTATUSFLG FROM TMG_DAILY a WHERE a.TDA_CCUSTOMERID = b.TDA_CCUSTOMERID AND a.TDA_CCOMPANYID = b.TDA_CCOMPANYID AND a.TDA_CEMPLOYEEID = b.TDA_CEMPLOYEEID AND a.TDA_DYYYYMMDD = b.TDA_DYYYYMMDD AND TMG_F_IS_HOLIDAY( b.TDA_CWORKINGID_R ,TDA_CCUSTOMERID ,TDA_CCOMPANYID) = 0 ) AS TDA_CSTATUSFLG_NAME, TDA_CNTFSTATUSFLG, TMG_F_GET_MGD_C( TDA_CNTFSTATUSFLG, TDA_DYYYYMMDD, 1, TDA_CCUSTOMERID, TDA_CCOMPANYID, #{language} ) AS TDA_CNTFSTATUSFLG_NAME, TDA_CWORKINGID_R, TMG_F_TRANS_WORKNAME( TDA_CEMPLOYEEID, TDA_DYYYYMMDD, 1, TDA_CCUSTOMERID, TDA_CCOMPANYID, #{language} ) AS TDA_CWORKINGID_R_NAME, TDA_CMESSAGE || ' ' || TDA_CCOMMENT_P AS CCOMMENT
${params}
FROM TMG_DAILY b WHERE TDA_CEMPLOYEEID = #{targetUser} AND TDA_DYYYYMM = #{month,jdbcType=DATE} AND TDA_CCOMPANYID = #{compCode} AND TDA_CCUSTOMERID = #{custID}
AND #{manageFlg} <>TMG_F_IS_MANAGEFLG( #{targetUser}, TRUNC(TO_DATE(#{month,jdbcType=DATE})), LAST_DAY(TO_DATE(#{month,jdbcType=DATE})), #{custID}, #{compCode} ) ORDER BY b.TDA_DYYYYMMDD

SELECT e.EMPNAME AS TDA_CEMPLOYEEID, TMG_F_CONV_MIN2HHMI ( TDA_NOPEN_N, 0 ) AS TDA_NOPEN_N, TMG_F_CONV_MIN2HHMI ( TDA_NCLOSE_N, 0 ) AS TDA_NCLOSE_N, TMG_F_CONV_MIN2HHMI ( TDA_NOPEN_O, 0 ) AS TDA_NOPEN_O, TMG_F_CONV_MIN2HHMI ( TDA_NCLOSE_O, 0 ) AS TDA_NCLOSE_O, TDA_CMESSAGE, TDA_CCOMMENT_O, TDA_CSTATUSFLG, e.EMPID AS TDA_CEMPLOYEEID, TMG_F_CONV_MIN2HHMI ( TDA_NOPEN_TP, 0 ) AS TDA_NOPEN_TP, TMG_F_CONV_MIN2HHMI ( TDA_NCLOSE_TP, 0 ) AS TDA_NCLOSE_TP, TMG_F_GET_MGD ( TDA_CWORKINGID_R, #{baseDay}, #{custId}, #{compId}, #{pLanguage} ) AS TDA_CWORKINGID_R, TMG_F_CONV_MIN2HHMI ( TDA_NRESTOPEN_N, 0 ) AS TDA_NRESTOPEN_N, TMG_F_CONV_MIN2HHMI ( TDA_NRESTCLOSE_N, 0 ) AS TDA_NRESTCLOSE_N, TMG_F_IS_EXCLUDE_OVERTIME ( e.EMPID, #{baseDay}, #{custId}, #{compId} ) AS EXCLUDE_OVERTIME, NVL( TDA_NOPEN_N, 0 ) AS NOPEN_N, NVL( TDA_NCLOSE_N, 0 ) AS NCLOSE_N, NVL( TDA_NOPEN_P, 0 ) AS NOPEN_P, NVL( TDA_NCLOSE_P, 0 ) AS NCLOSE_P, ( SELECT NVL( SUM( TDAD_NCLOSE - TDAD_NOPEN ), 0 ) FROM TMG_DAILY_DETAIL TDD WHERE TDAD_CEMPLOYEEID = TDA_CEMPLOYEEID AND TDAD_DYYYYMMDD = #{baseDay} AND TDAD_CCOMPANYID = TDA_CCOMPANYID AND TDAD_CCUSTOMERID = TDA_CCUSTOMERID AND TDAD_CNOTWORKID = 'TMG_ITEMS|NoticeRest' ) AS SUM_REST, ( SELECT NVL( SUM( TDAD_NCLOSE - TDAD_NOPEN ), 0 ) FROM TMG_DAILY_DETAIL TDD WHERE TDAD_CEMPLOYEEID = TDA_CEMPLOYEEID AND TDAD_DYYYYMMDD = #{baseDay} AND TDAD_CCOMPANYID = TDA_CCOMPANYID AND TDAD_CCUSTOMERID = TDA_CCUSTOMERID AND TDAD_CNOTWORKID LIKE 'TMG_NOTWORK%' AND ( ( NVL( TDA_NOPEN_N, 0 ) <= NVL( TDAD_NOPEN, 0 ) AND NVL( TDA_NCLOSE_N, 0 ) > NVL( TDAD_NOPEN, 0 ) ) AND ( NVL( TDA_NOPEN_N, 0 ) < NVL( TDAD_NCLOSE, 0 ) AND NVL( TDA_NCLOSE_N, 0 ) >= NVL( TDAD_NCLOSE, 0 ) ) ) ) AS SUM_NOTICE, ( SELECT COUNT( TDAD_CNOTWORKID ) FROM TMG_DAILY_DETAIL TDD WHERE TDAD_CEMPLOYEEID = TDA_CEMPLOYEEID AND TDAD_DYYYYMMDD = #{baseDay} AND TDAD_CCOMPANYID = TDA_CCOMPANYID AND TDAD_CCUSTOMERID = TDA_CCUSTOMERID AND TDAD_CNOTWORKID = 'TMG_ITEMS|PlanRest' ) AS CNT_PLANREST,
${itemsSql.mgdCsql} AS ${itemsSql.mgdCcolumnid}
FROM TMG_DAILY d, (${sql}) e WHERE d.TDA_CCUSTOMERID = #{custId} AND d.TDA_CCOMPANYID = #{compId} AND d.TDA_DYYYYMM = #{baseMonth} AND d.TDA_DYYYYMMDD = #{baseDay} AND d.TDA_CEMPLOYEEID = e.EMPID AND d.TDA_DSTARTDATE <= #{baseDay} AND d.TDA_DENDDATE >= #{baseDay} AND 'TMG_MANAGEFLG|0' <> TMG_F_IS_MANAGEFLG ( e.EMPID, TRUNC( TO_DATE(#{baseMonth}) ), LAST_DAY( TO_DATE( #{baseMonth} ) ), e.cust, e.comp ) ORDER BY e.seq
