mapper.select:
SELECT
    P.TPPA_NAVGWORKTIME
FROM
    TMG_PERSONAL_PATTERN P
WHERE
        P.TPPA_CCUSTOMERID     =   #{customerId}
AND     P.TPPA_CCOMPANYID      =   #{companyId}
AND     P.TPPA_CEMPLOYEEID     =   #{employeeId}
AND     P.TPPA_DSTARTDATE      <=  #{yyyymmdd,jdbcType=DATE}
AND     P.TPPA_DENDDATE        >=  #{yyyymmdd,jdbcType=DATE}
;
